template.content:
package ${packageName}.spi;

import ${packageName}.common.bean.BaseResult;
import ${packageName}.domain.dto.${ClassName}Dto;
import ${packageName}.domain.query.${ClassName}Query;
import org.springframework.cloud.openfeign.FeignClient;
import org.springframework.http.MediaType;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;

import java.util.List;

/**
 * Service(${functionName})
 * @author ${functionAuthor}
 * @version ${functionVersion}
 */
@FeignClient(value = "ai-data-resource-service")
public interface ${ClassName}RestService  {


    /**
     * 获取带分页数据
     *
     * @param ${className}Query
     * @return ${ClassName}Query
     */
    @RequestMapping(value = "/${className}/get${ClassName}ListByPage", consumes = MediaType.APPLICATION_JSON_VALUE)
    BaseResult<${ClassName}Query> get${ClassName}ListByPage(@RequestBody ${ClassName}Query ${className}Query);

    /**
     * 不带分页数据
     *
     * @param ${className}Dto
     * @return List<${ClassName}Dto>
     */
    @RequestMapping(value = "/${className}/get${ClassName}List", consumes = MediaType.APPLICATION_JSON_VALUE)
    BaseResult<List<${ClassName}Dto>> get${ClassName}List(@RequestBody ${ClassName}Dto ${className}Dto);

    /**
     * 保存数据
     *
     * @param ${className}Dto
     * @return int
     */
    @RequestMapping(value = "/${className}/save", consumes = MediaType.APPLICATION_JSON_VALUE)
    BaseResult<Integer> save(@RequestBody ${ClassName}Dto ${className}Dto);

    /**
     * 查询单个实体数据
     *
     * @param ${className}Dto
     * @return ${ClassName}Dto
     */
    @RequestMapping(value = "/${className}/get${ClassName}", consumes = MediaType.APPLICATION_JSON_VALUE)
    BaseResult<${ClassName}Dto> get${ClassName}(@RequestBody ${ClassName}Dto ${className}Dto);

    /**
     * 根据编号查询单个实体
     *
     * @param ${className}Id
     * @return ${ClassName}Dto
     */
    @RequestMapping(value = "/${className}/get${ClassName}ById", consumes = MediaType.APPLICATION_JSON_VALUE)
    BaseResult<${ClassName}Dto> get${ClassName}ById(@RequestParam("${className}Id") String ${className}Id);

    /**
     * 删除数据
     *
     * @param id
     * @return boolean
     */
    @RequestMapping(value = "/${className}/delete", consumes = MediaType.APPLICATION_JSON_VALUE)
    boolean delete(@RequestParam("id") Long id);

}
