package ${packageName}.service;

import com.google.common.collect.Lists;
import ${packageName}.mapper.${ClassName}Mapper;
import ${packageName}.domain.dto.${ClassName}Dto;
import ${packageName}.po.${ClassName};
import ${packageName}.domain.query.${ClassName}Query;
import com.wekj.common.util.IdGenerator;
import lombok.extern.slf4j.Slf4j;
import org.apache.commons.lang3.StringUtils;
import org.springframework.beans.BeanUtils;
import org.springframework.stereotype.Service;

import javax.annotation.Resource;
import java.time.LocalDateTime;
import java.util.List;
import java.util.stream.Collectors;

/**
 * Service(${functionName})
 * @author ${functionAuthor}
 * @version ${functionVersion}
 */
@Service
@Slf4j
public class ${ClassName}Service {

    @Resource
    private ${ClassName}Mapper ${className}Mapper;
    private static final String PREFIX = "${ClassName}";

    /**
     * get${ClassName}ListByPage(获取带分页数据)
     *
     * @param ${className}Query
     * @return ${ClassName}Query
     * @author andi
     */
    public ${ClassName}Query get${ClassName}ListByPage(${ClassName}Query ${className}Query) {
        if (${className}Query == null) {
            return null;
        }
        ${ClassName}Query result = new ${ClassName}Query();
        Long totalCount = getTotalCount(${className}Query);
        if (totalCount <= 0) {
            result.setResults(Lists.newArrayList());
            result.setTotal(0L);
            return result;
        }
        List<${ClassName}> ${className}List = ${className}Mapper.get${ClassName}ListByPage(${className}Query);
        List<${ClassName}Dto> list = ${className}List.stream()
                .map(${ClassName}Service::do2Dto)
                .collect(Collectors.toList());
        result.setTotal(totalCount);
        result.setResults(list);
        return result;
    }

    /**
     * get${ClassName}List(获取${functionName}不带分页数据)
     *
     * @param ${className}Dto
     * @return List<${ClassName}Dto>
     * @author andi
     */
    public List<${ClassName}Dto> get${ClassName}List(${ClassName}Dto ${className}Dto) {
        if (${className}Dto == null) {
            return Lists.newArrayList();
        }
        return ${className}Mapper.get${ClassName}List(dto2Do(${className}Dto)).stream()
                .map(${ClassName}Service::do2Dto)
                .collect(Collectors.toList());
    }


    /**
     * save (保存数据)
     *
     * @param ${className}Dto
     * @return int
     * @author andi
     */
    public int save(${ClassName}Dto ${className}Dto) {
        if (${className}Dto == null) {
            return 0;
        }
        if (${className}Dto.getId() == null || ${className}Dto.getId() < 1) {
            ${ClassName}Dto ${className}ById = get${ClassName}ById(${className}Dto.get${ClassName}Id());
            if (${className}ById != null) {
                ${className}Dto.setId(${className}ById.getId());
            }
        }
        ${ClassName} ${className} = dto2Do(${className}Dto);
        if (${className}Dto.getId() == null || ${className}Dto.getId() < 1) {
            ${className}.set${ClassName}Id(IdGenerator.generate(PREFIX));
            ${className}.setGmtModified(LocalDateTime.now());
            ${className}.setGmtCreate(LocalDateTime.now());
            return ${className}Mapper.add(${className});
        }
        ${className}.setGmtModified(LocalDateTime.now());
        return ${className}Mapper.updateById(${className});
    }

    /**
     * get${ClassName}(获取单条数据)
     *
     * @param ${className}Dto
     * @return ${ClassName}Dto
     * @author andi
     */
    public ${ClassName}Dto get${ClassName}(${ClassName}Dto ${className}Dto) {
        if (${className}Dto == null) {
            return null;
        }
        ${ClassName} ${className} = dto2Do(${className}Dto);
        return do2Dto(${className}Mapper.getSingle${ClassName}(${className}));
    }


    /**
     * get${ClassName}ById(获取单条数据)
     *
     * @param ${className}Id
     * @return ${ClassName}Dto
     * @author andi
     */
    public ${ClassName}Dto get${ClassName}ById(String ${className}Id) {
        if (StringUtils.isBlank(${className}Id)) {
            return null;
        }
        ${ClassName} param = new ${ClassName}();
        param.set${ClassName}Id(${className}Id);
        return do2Dto(${className}Mapper.getSingle${ClassName}(param));
    }

    /**
     * delete(逻辑删除数据)
     *
     * @param id
     * @return boolean
     * @author andi
     */
    public boolean delete(Long id) {
        if (id < 1) {
            return false;
        }
        ${ClassName} dt = ${className}Mapper.get${ClassName}ById(id);
        if (dt == null) {
            return false;
        }
        ${className}Mapper.delete(id);
        return true;
    }

    /**
     * 获取分页总记录数
     *
     * @param ${className}Query
     * @return Long
     */
    private Long getTotalCount(${ClassName}Query ${className}Query) {
        Long totalCount = ${className}Mapper.getTotalCount(${className}Query);
        if (totalCount == null) {
            return 0L;
        }
        return totalCount;
    }

    private static ${ClassName}Dto do2Dto(${ClassName} ${className}) {
        if (${className} == null) {
            return null;
        }
        ${ClassName}Dto ${className}Dto = new ${ClassName}Dto();
        BeanUtils.copyProperties(${className}, ${className}Dto);
        return ${className}Dto;
    }

    private static ${ClassName} dto2Do(${ClassName}Dto ${className}Dto) {
        if (${className}Dto == null) {
            return null;
        }
        ${ClassName} ${className} = new ${ClassName}();
        BeanUtils.copyProperties(${className}Dto, ${className});
        return ${className};
    }

}
