template.content:
package ${packageName}.adr.spi;

import ${packageName}.adr.dto.${ClassName}Dto;
import ${packageName}.adr.query.${ClassName}Query;
import org.springframework.cloud.netflix.feign.FeignClient;
import org.springframework.http.MediaType;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;

import java.util.List;

/**
 * Service(${functionName})
 * @author ${functionAuthor}
 * @version ${functionVersion}
 */
@FeignClient(value = "ai-data-resource-service")
public interface ${ClassName}RestService  {


    /**
     * list:(查询${functionName}带分页数据)
     *
     * @return ${ClassName}Query
     * @Author ${functionAuthor}
     */
    @RequestMapping(value = "/${className}/get${ClassName}ListByPage", consumes = MediaType.APPLICATION_JSON_UTF8_VALUE)
    ${ClassName}Query get${ClassName}ListByPage(@RequestBody ${ClassName}Query ${className}Query);

    /**
     * get${ClassName}List:(查询${functionName}不带分页数据)
     *
     * @return List<${ClassName}Dto>
     * @Author ${functionAuthor}
     */
    @RequestMapping(value = "/${className}/get${ClassName}List", consumes = MediaType.APPLICATION_JSON_UTF8_VALUE)
    List<${ClassName}Dto> get${ClassName}List(@RequestBody ${ClassName}Dto ${className}Dto);

    /**
     * get${ClassName}:(查询单个实体数据)
     *
     * @return ${ClassName}Dto
     * @Author ${functionAuthor}
     */
    @RequestMapping(value = "/${className}/get${ClassName}", consumes = MediaType.APPLICATION_JSON_UTF8_VALUE)
    ${ClassName}Dto get${ClassName}(@RequestBody ${ClassName}Dto ${className}Dto);

    /**
     * get${ClassName}ById:(根据编号查询单个实体)
     *
     * @return ${ClassName}Dto
     * @param ${className}Id
     */
    @RequestMapping(value = "/${className}/get${ClassName}Id", consumes = MediaType.APPLICATION_JSON_UTF8_VALUE)
    ${ClassName}Dto get${ClassName}ById(@RequestParam("${className}Id") String ${className}Id);

    /**
     * save:(保存数据)
     *
     * @return int
     * @Author ${functionAuthor}
     */
    @RequestMapping(value = "/${className}/save", consumes = MediaType.APPLICATION_JSON_UTF8_VALUE)
    int save(@RequestBody ${ClassName}Dto ${className}Dto);

    /**
     * delete:(删除数据)
     *
     * @return boolean
     * @Author ${functionAuthor}
     */
    @RequestMapping(value = "/${className}/delete", consumes = MediaType.APPLICATION_JSON_UTF8_VALUE)
    boolean delete(@RequestParam("id") Long id);

}
